INSERT INTO broadcast (id, time, likes, title, lead, content, accepted, journalist_id)
VALUES (2, "2014-07-01 11:00:00", 15, "Торжественно открыли городской пикник", "Прошло торжественное открытие пикника", "<p>Состоялось открытие ГОРОДСКОГО ПИКНИКА</p><p>Стартовал третий городской пикник. Рэд сидит, глядя на Золотой шар, и думает: попросить о дочери, а ещё о чем? И с ужасом понимает, что нет у него ни слов, ни мыслей — все он растерял в своих сталкерских вылазках, стычках с охранниками, погоне за деньгами — семью кормить надо, а умеет он только ходить в Зону да сбывать диковинные штучки всяким темным людям, которые неизвестно как ими распоряжаются. И Рэд понимает, что других слов, кроме тех, что выкрикнул перед смертью этот мальчик, так не похожий на своего Стервятника-отца, ему не придумать: «Счастье для всех, даром, и пусть никто не уйдёт обиженный!»</p>", 1, 1);
